def factorial(n):
    """Calculates the factorial of a non-negative integer."""
    if n == 0:
        return 1
    else:
        result = 1
        for i in range(1, n + 1):
            result *= i
        return result

def main():
    p = int(input("Enter the starting integer (p): "))
    q = int(input("Enter the ending integer (q): "))

    if p < 0 or q < 0 or p > q:
        print("Invalid input. Please ensure 0 <= p <= q.")
        return

    print(f"{p} {q}")  # Print the range as specified
    for i in range(p, q + 1):
        print(f"{i}! = {factorial(i)}")


if __name__ == "__main__":
    main()

