  const doc = parser.parseFromString(html, "text/html");
  const root = doc.documentElement

  // Extract the artist and album title
  const artist = root.querySelector "#artist"
  const albumTitle = root.querySelector "#albumTitle"

  // Extract the tracklist and duration
  const tracks = root.querySelectorAll "#tracklist > li"
  const trackDurations = []
  tracks.forEach((track, index) => {
    const trackTitle = track.querySelector "#trackTitle"
    const duration = track.querySelector "#trackDuration"
    trackDurations.push({
      title: trackTitle.textContent,
      duration: duration.textContent
    })
  })

  // Extract the record label information
  const label = root.querySelector "#label"

  // Return the extracted information as a JSON object
  return {
    artist: artist.textContent,
    albumTitle: albumTitle.textContent,
    tracklist: trackDurations,
    recordLabel: label.textContent
  }
}
```
